import React, { useState, useEffect } from 'react';

const AlumniForm = ({ initialValues = {}, onSubmit }) => {
    const [formValues, setFormValues] = useState({
        name: '',
        email: '',
        graduationYear: '',
        company: '',
        linkedinProfile: '',
        photo: null,
        motivationMessage: '',
        ...initialValues
    });

    useEffect(() => {
    setFormValues({
        name: initialValues.name || '',
        email: initialValues.email || '',
        graduationYear: initialValues.graduationYear || '',
        company: initialValues.company || '',
        linkedinProfile: initialValues.linkedinProfile || '',
        photo: initialValues.photo || null,
        motivationMessage: initialValues.motivationMessage || ''
    });
}, [initialValues]);


    const handleChange = (e) => {
        const { name, value, files } = e.target;
        setFormValues({
            ...formValues,
            [name]: name === 'photo' ? files[0] : value
        });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        Object.keys(formValues).forEach(key => {
            formData.append(key, formValues[key]);
        });

        try {
            await onSubmit(formData);
        } catch (error) {
            console.error('Error submitting form', error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name"
                value={formValues.name}
                onChange={handleChange}
                placeholder="Name"
                required
            />
            <input
                type="email"
                name="email"
                value={formValues.email}
                onChange={handleChange}
                placeholder="Email"
                required
            />
            <input
                type="number"
                name="graduationYear"
                value={formValues.graduationYear}
                onChange={handleChange}
                placeholder="Graduation Year"
                required
            />
            <input
                type="text"
                name="company"
                value={formValues.company}
                onChange={handleChange}
                placeholder="Company"
                required
            />
            <input
                type="text"
                name="linkedinProfile"
                value={formValues.linkedinProfile}
                onChange={handleChange}
                placeholder="LinkedIn Profile"
            />
            <input
                type="file"
                name="photo"
                onChange={handleChange}
            />
            <textarea
                name="motivationMessage"
                value={formValues.motivationMessage}
                onChange={handleChange}
                placeholder="Motivation Message"
            />
            <button type="submit">Submit</button>
        </form>
    );
};

export default AlumniForm;
