import React, { useEffect, useState } from 'react';
import AlumniList from '../components/AlumniList'; 
import axios from 'axios';

const AlumniPage = () => {
    const [alumniList, setAlumniList] = useState([]);
    const [loading, setLoading] = useState(true); // Added loading state
    const [error, setError] = useState(null);     // Added error state

    useEffect(() => {
        const fetchAlumni = async () => {
            try {
                const response = await axios.get('http://localhost:5000/api/admin/alumni');
                setAlumniList(response.data);
                setLoading(false); // Data is fetched, stop loading
            } catch (error) {
                console.error('Error fetching alumni:', error);
                setError('Failed to fetch alumni data.'); // Set error message
                setLoading(false); // Stop loading on error
            }
        };

        fetchAlumni();
    }, []);

    if (loading) return <p>Loading alumni...</p>; // Show loading indicator

    if (error) return <p>{error}</p>; // Show error message

    return (
        <div>
            <h1>Alumni List</h1>
            {alumniList.length > 0 ? (
                <AlumniList alumniList={alumniList} />
            ) : (
                <p>No alumni found.</p> // Show message when no alumni data
            )}
        </div>
    );
};

export default AlumniPage;
